mapper.select:
SELECT b.BACKLOG_NO , b.BACKLOG_NAME , b.BACKLOG_CONTENT , b.BACKLOG_PRIORITY , b.BACKLOG_STATUS , b.PROJECT_NO FROM ( SELECT a.*, ROWNUM R FROM ( SELECT * FROM BACKLOG WHERE PROJECT_NO = #{projectNo}
and( BACKLOG_NAME LIKE #{searchValue} OR BACKLOG_CONTENT LIKE #{searchValue} OR BACKLOG_PRIORITY LIKE #{searchValue} OR BACKLOG_STATUS LIKE #{searchValue} )
) a ) b WHERE b.R BETWEEN #{pageInfo.startRow} AND #{pageInfo.endRow} ORDER BY BACKLOG_NO ASC

SELECT
        COUNT(*)
FROM
        BACKLOG
WHERE
        PROJECT_NO = ${projectNo}

    and(
    BACKLOG_NAME
    LIKE #{searchValue}
    OR BACKLOG_CONTENT
    LIKE #{searchValue}
    OR BACKLOG_PRIORITY
    LIKE #{searchValue}
    OR BACKLOG_STATUS
    LIKE #{searchValue}
    )

ORDER BY BACKLOG_NO ASC

mapper.update:
 UPDATE BACKLOG
 SET
 BACKLOG_NAME = #{backlogName}
 ,BACKLOG_CONTENT = #{backlogContent}
 ,BACKLOG_STATUS = #{backlogStatus}
, BACKLOG_PRIORITY = #{backlogPriority}
 ,PROJECT_NO = #{projectNo}
 WHERE BACKLOG_NO = #{backlogNo}
