mapper.insert:
INSERT
  INTO ROLE_AUTHORITY_TYPE
(
  ROLE_NO
, AUTHORITY_NO
)
(
SELECT #{roleNo}
     , AUTHORITY_NO
  FROM AUTHORITY
 WHERE AUTHORITY_STATE =#{state}
   AND TYPE_NO = #{typeNo}
)

mapper.update:
UPDATE ROLE
   SET ROLE_NAME = #{roleName}
 WHERE ROLE_NO = #{roleNo}

mapper.insert:
INSERT
  INTO AUTHORITY_TYPE
(
  TYPE_NO
, TYPE_NAME
)
VALUES
(
  ( SELECT MAX(TYPE_NO)+1 TYPE_NO
      FROM AUTHORITY_TYPE
  )
, #{typeName}
)

mapper.select:
SELECT TYPE_NO
  FROM AUTHORITY_TYPE
 WHERE TYPE_NAME = #{typeName}

mapper.delete:
DELETE
  FROM ROLE_AUTHORITY_TYPE
 WHERE AUTHORITY_NO IN ( SELECT TYPE_NO
                           FROM AUTHORITY
                          WHERE TYPE_NO = #{typeNo}
                       )
