mapper.select:
SELECT
       E.EMPLOYEE_NO
     , E.EMPLOYEE_NAME
     , E.EMPLOYEE_EMAIL
     , E.ROLE_NAME
  FROM ( SELECT ROWNUM R
              , D.*
           FROM ( SELECT A.EMPLOYEE_NO
                       , A.EMPLOYEE_NAME
                       , A.EMPLOYEE_EMAIL
                       , C.ROLE_NAME
                    FROM EMPLOYEE A
                    LEFT JOIN CREATED_PROJECT B ON ( A.EMPLOYEE_NO = B.EMPLOYEE_NO )
                    LEFT JOIN ROLE C ON ( B.ROLE_NO = C.ROLE_NO )

                        WHERE A.EMPLOYEE_NO LIKE #{search}
                           OR A.EMPLOYEE_NAME LIKE #{search}
                           OR A.EMPLOYEE_EMAIL LIKE #{search}
                           OR C.ROLE_NAME LIKE #{search}

                   ORDER BY A.EMPLOYEE_NO
                ) D
       ) E
  WHERE E.R BETWEEN #{start} AND #{end}

mapper.insert:
INSERT
  INTO EMPLOYEE A
(
  A.EMPLOYEE_NO
, A.EMPLOYEE_NAME
, A.EMPLOYEE_EMAIL
, A.EMPLOYEE_PASSWORD
)
VALUES(
  #{no}
, #{name}
, #{email}
, #{pwd}
)

INSERT
  INTO CREATED_PROJECT A
(
  A.EMPLOYEE_NO
, A.ROLE_NO
)
VALUES(
  #{no}
, 1
)
